import "./styles.css";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";
import ArrowDownwardIcon from "@mui/icons-material/ArrowDownward";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import PauseIcon from "@mui/icons-material/Pause";
import RestartAltIcon from "@mui/icons-material/RestartAlt";
import { useState, useEffect } from "react";

export default function App() {
  const [breakLength, setBreakLength] = useState(5);
  const [sessionLength, setSessionLength] = useState(25);
  const [counter, setCounter] = useState(1500);
  const [currentTime, setCurrentTime] = useState("Session");
  const [timer, setTimer] = useState("stop");

  let minute = Math.floor(counter / 60)
    .toString()
    .padStart(2, 0);
  let second = Math.floor(counter - minute * 60)
    .toString()
    .padStart(2, 0);

  function beep() {
    var snd = new Audio(
      "data:audio/wav;base64,"
    );
    snd.play();
  }
  const timeout = setTimeout(() => {
    if (counter > 0 && timer === "start") {
      setCounter(counter - 1);
    }
  }, 1000);
  useEffect(() => {
    if (timer === "stop") {
      return () => clearTimeout(timeout);
    }
  }, [counter, timer]);

  useEffect(() => {
    setCounter(sessionLength * 60);
  }, [sessionLength]);
  useEffect(() => {
    if (counter === 0 && currentTime === "Session") {
      beep();
      setCurrentTime("Break");
      setCounter(breakLength * 60);
      setTimer("start");
    } else if (counter === 0 && currentTime === "Break") {
      beep();
      setCurrentTime("Session");
      setCounter(sessionLength * 60);
      setTimer("start");
    }
  }, [counter, currentTime, breakLength, sessionLength]);

  const onIncreaseBreak = () => {
    setBreakLength(breakLength + 1);
    breakLength >= 59 && setBreakLength(60);
  };
  const onIncreaseSession = () => {
    setSessionLength(sessionLength + 1);
    sessionLength >= 59 && setSessionLength(60);
  };
  const onDecreaseBreak = () => {
    setBreakLength(breakLength - 1);
    breakLength <= 1 && setBreakLength(1);
  };
  const onDecreaseSession = () => {
    setSessionLength(sessionLength - 1);
    sessionLength <= 1 && setSessionLength(1);
  };
  const onReset = () => {
    setCounter(1500);
    setTimer("stop");
    setSessionLength(25);
    setBreakLength(5);
    setCurrentTime("Session");
  };

  return (
    <div className="App">
      <div className="title">25+5 Clock</div>
      <div className="label">
        <div>
          <div id="break-label">Break Length</div>
          <div>
            <ArrowUpwardIcon onClick={onIncreaseBreak} id="break-increment" />
            <span id="break-length">{breakLength}</span>
            <ArrowDownwardIcon onClick={onDecreaseBreak} id="break-decrement" />
          </div>
        </div>
        <div>
          <div id="session-label">Session Length</div>
          <div>
            <ArrowUpwardIcon
              onClick={onIncreaseSession}
              id="session-increment"
            />
            <span id="session-length">{sessionLength}</span>
            <ArrowDownwardIcon
              onClick={onDecreaseSession}
              id="session-decrement"
            />
          </div>
        </div>
      </div>
      <div id="timer-label">
        <div>{currentTime}</div>
        <div id="time-left">
          {minute}:{second}
        </div>
      </div>
      <div>
        <PlayArrowIcon
          onClick={() => {
            setTimer("start");
          }}
          id="start_stop"
        />
        <PauseIcon
          id="start_stop"
          onClick={() => {
            setTimer("stop");
          }}
        />
        <RestartAltIcon onClick={onReset} />
      </div>
    </div>
  );
}
